<!-- We need a settings page --><div clas="gf-settings"><h2>Settings</h2><form class="standard-form" action="" method="post"><table class="gf-settings-table"><?php $gf_settings=gf_get_settings();?><tr>	<td>		<strong><?php _e("Enable Posting to Activity","gf");?></strong>	</td><td class='floatit'>	<label><input type="radio" name="enable_activity" value="yes" <?php if($gf_settings["enable_activity"]=="yes"):?> checked="checked"<?php endif;?> ><?php _e("Yes","gf");?></label>	<label><input type="radio" name="enable_activity" value="no" <?php if($gf_settings["enable_activity"]!="yes"):?> checked="checked"<?php endif;?> ><?php _e("No","gf");?></label><div class="clear"></div>	</td></tr></table><?php wp_nonce_field("gf-settings");?><input type="submit" value="<?php _e('Save Settings','gf');?>" name="save_settings" /></form></div><!-- we will have in next version -->